insert into T_Eg_Book (C_ID, C_NAME, C_AUTH, C_PUBLISHER, F_PRICE, D_PUBLISHER, D_BUY, C_LIBRARYID, C_ISBN, C_VERSION, I_PHOTO, T_ABST, N_VALID, T_INVALIDREASON, N_TYPE, D_BOOKING, N_BOOKINGUSERID, N_LENDUSERID, D_LEND ) values ('1000000000004127'                 ,'论语别裁'                             ,'南怀瑾'                              ,'复旦大学出版社'                          ,42        ,'2007-07-05 12:00:00' ,'2008-04-16 12:00:00' ,'19F540A7EDF8D7CCAF162B14E6A05966' ,'9787309032437'                    ,NULL,NULL,'内容简介
由孔子的弟子及其后学编集的《论语》，是一部记载孔子言语行事的重要儒家经典。自西汉以来，《论语》所表述的孔子学说，迅速扩展，其影响遍及政治、思想、文化、教育、伦理道德等各个领域，成为中国传统文化的一大标识。有关《论语》的章句注流也累代不绝。台湾著名学者南怀瑾先生以渊博的学识，旁征博引，拈提古今而撰成的《论语别裁》，就是关于《论语》的讲演录。书中对《论语》二十篇的每一段原文，都作了详细而又生动的讲述。不仅有篇章结构、段落联结上的提示，而且有原文义旨以及所涉人文掌故的阐发。尤为新颖的是，作者将对原文的串讲撮编为一个个历史故事，蕴意深邃而妙趣横生，这在众多的《论语》章疏中是别具一格的。原书由台湾老古文化事业公司于1976年5月出版，迄至1988年，已印了十八版。为弘扬海峡两岸同源的中国文化，兹征得作者和原出版单位同意，将《论语别裁》第十八版改排出版，以供研究。
我们要了解传统文化，首先必须要了解儒家的学术思想。要讲儒家的思想，首先便要研究孔孟的学术。要讲孔子的思想学术，必须先要了解《论语》。比照现代世界上的国际间文化潮流，对于自己民族、国家和历史，确定今后应该要走的路线和方向。因此若能使一般陷于现代社会心理病态的人们，在我们讲的文字言语以外去体会，能够求得一个解脱的答案，建立一种卓然不拔，矗立于风雨艰危中的人生目的和精神，这便是我所要馨香祷祝的了。' ,1         ,NULL,1         ,NULL,NULL,NULL,NULL);

insert into T_Eg_Book (C_ID, C_NAME, C_AUTH, C_PUBLISHER, F_PRICE, D_PUBLISHER, D_BUY, C_LIBRARYID, C_ISBN, C_VERSION, I_PHOTO, T_ABST, N_VALID, T_INVALIDREASON, N_TYPE, D_BOOKING, N_BOOKINGUSERID, N_LENDUSERID, D_LEND ) values ('1000000000004128'                 ,'国学拾遗'                             ,'林语堂'                              ,'陕西师范大学出版社'                        ,27        ,'2008-01-08 12:00:00' ,'2008-07-07 12:00:00' ,'B31E784BD0C115FF2FF7157007843A75' ,'9787561343340'                    ,NULL,NULL,'内容简介
一个人彻悟的程度，恰等于他所受痛苦的深度。
《国学拾遗》包括儒家：仁者爱人、道家：大道无形和佛家：无生无灭三部分。
“仁”，或真人性，在道德感的形式上，是以人的内心和外在的宇宙的道德相和谐为基础。当这个“真义”实现时，便“天地位焉，万物育焉”。这就是儒家的哲学基础。
道家哲学和儒家哲学的涵义，一个代表消极的人生观，一个代表积极的人生观，那么，我相信这两种哲学不仅是中国人有之，而也是人类天性所固有的东西。
我们大家都是生就一半道家主义，一半儒家主义。
道家与儒家，不过是中国人灵魂的两面。一种属于活动的、有为的、相信的一面；一种是属于静观的、怀疑的、惊异的，使生活笼罩着一种如梦性质的一面。这样是很好的。
佛教教导人生是一个生死轮回的圆圈，我们现在为我们前生所做的接受惩罚，我们将来我们现在所做的接受惩罚。这个圆圈是痛苦的和一个永不休止、应接不暇的幻影。人要逃脱这个圆圈才能获得拯救。' ,1         ,NULL,1         ,NULL,NULL,NULL,NULL);

insert into T_Eg_Book (C_ID, C_NAME, C_AUTH, C_PUBLISHER, F_PRICE, D_PUBLISHER, D_BUY, C_LIBRARYID, C_ISBN, C_VERSION, I_PHOTO, T_ABST, N_VALID, T_INVALIDREASON, N_TYPE, D_BOOKING, N_BOOKINGUSERID, N_LENDUSERID, D_LEND ) values ('1000000000004129'                 ,'读库0802'                           ,'张立宪'                              ,'新星出版社'                            ,33        ,'2008-01-05 12:00:00' ,'2008-07-07 12:00:00' ,'B31E784BD0C115FF2FF7157007843A75' ,'9787802254855'                    ,NULL,NULL,'目录
